function countDescendants(person, allData) {
    // Count all descendants of a person
    let count = 0;
    const personKey = getPersonKey(person);

    allData.forEach(p => {
        const parent = getParentPerson(p);
        if (parent && getPersonKey(parent) === personKey) {
            count += 1 + countDescendants(p, allData);
        }
    });

    return count;
}

function isDescendantOf(person, ancestorName) {
    // Check if person is a descendant of someone with ancestorName
    if (person.name === ancestorName) return true;

    let current = person;
    while (current) {
        if (current.name === ancestorName) return true;
        current = getParentPerson(current);
    }
    return false;
}

