// Handle search
function handleSearch(event) {
    const query = event.target.value.trim().toLowerCase();
    const resultsContainer = document.getElementById('search-results');

    // Clear previous highlights
    clearSearchHighlights();

    if (query.length < 2) {
        resultsContainer.innerHTML = '';
        resultsContainer.style.display = 'none';
        return;
    }

    // Find matching nodes
    const matches = familyData.filter(person =>
        person.name.toLowerCase().includes(query)
    );

    if (matches.length === 0) {
        resultsContainer.style.display = 'block';
        resultsContainer.innerHTML = '<div class="no-results">No matches found</div>';
        return;
    }

    // Show results container
    resultsContainer.style.display = 'block';

    // Helper function to get parent info
    const getParentInfo = (person) => {
        if (!person.parents || person.parents.length === 0) {
            return 'Root';
        }

        const parentGen = person.generation - 1;
        const parent = familyData.find(p =>
            p.generation === parentGen &&
            JSON.stringify(p.parents) === JSON.stringify(person.parents.slice(0, -1))
        );

        return parent ? `Child of ${parent.name}` : '';
    };

    // Group results by name to detect duplicates
    const nameGroups = {};
    matches.forEach(person => {
        if (!nameGroups[person.name]) {
            nameGroups[person.name] = [];
        }
        nameGroups[person.name].push(person);
    });

    // Display results
    resultsContainer.innerHTML = matches.map(person => {
        const duplicateCount = nameGroups[person.name].length;
        const duplicateIndex = nameGroups[person.name].indexOf(person) + 1;
        const parentInfo = getParentInfo(person);
        // Create a unique identifier using parents array
        const uniqueId = person.parents ? JSON.stringify(person.parents) : `gen-${person.generation}`;

        return `
        <div class="search-result-item" data-name="${person.name}" data-generation="${person.generation}" data-unique-id='${uniqueId}'>
            <div style="flex: 1; min-width: 0;">
                <div style="display: flex; align-items: center; gap: 6px;">
                    <span class="result-name" style="flex: 0 1 auto; white-space: nowrap;">${person.name}</span>
                    ${duplicateCount > 1 ? `<span style="display: inline-block; background: #667eea; color: white; font-size: 0.7rem; font-weight: 600; padding: 2px 6px; border-radius: 10px; white-space: nowrap;">${duplicateIndex}/${duplicateCount}</span>` : ''}
                </div>
                ${parentInfo ? `<div style="font-size: 0.75rem; color: #999; margin-top: 3px;">${parentInfo}</div>` : ''}
            </div>
            <span class="gen-label" style="margin-left: 10px;">Gen ${person.generation}</span>
        </div>
    `;
    }).join('');

    // Add click listeners to results
    resultsContainer.querySelectorAll('.search-result-item').forEach(item => {
        item.addEventListener('click', () => {
            const name = item.getAttribute('data-name');
            const generation = parseInt(item.getAttribute('data-generation'));
            const uniqueId = item.getAttribute('data-unique-id');
            console.log('Search result clicked:', name, generation, 'calling zoomToNode with showModal=true');

            // Close search panel and results
            const searchPanel = document.getElementById('search-panel');
            const searchInput = document.getElementById('search-input');
            searchPanel.classList.remove('active');
            resultsContainer.style.display = 'none';
            searchInput.value = '';
            clearSearchHighlights();
            resultsContainer.innerHTML = '';

            zoomToNode(name, generation, uniqueId, true); // Pass true to show modal after zoom
        });
    });
}

// Zoom to and highlight a specific node
function zoomToNode(name, generation, uniqueId, showModal = false) {
    // Find the node group - use uniqueId for precise matching if available
    let nodeGroup;

    if (uniqueId) {
        // Find by matching the parents array (most precise)
        nodeGroup = document.querySelector(`.person-node[data-person-name="${name}"][data-person-generation="${generation}"][data-person-parents='${uniqueId}']`);
    }

    if (!nodeGroup && generation) {
        // Fallback: find by name and generation
        nodeGroup = document.querySelector(`.person-node[data-person-name="${name}"][data-person-generation="${generation}"]`);
    }

    if (!nodeGroup) {
        // Final fallback: just by name
        nodeGroup = document.querySelector(`.person-node[data-person-name="${name}"]`);
    }

    if (!nodeGroup) {
        console.error('Node not found:', name, generation, uniqueId);
        return;
    }

    // Get node position
    const nodeX = parseFloat(nodeGroup.getAttribute('data-person-x'));
    const nodeY = parseFloat(nodeGroup.getAttribute('data-person-y'));

    // Clear previous highlights
    clearSearchHighlights();

    // Highlight the node (outline only)
    nodeGroup.classList.add('highlighted');

    // Add ripple effect circles with bounce animation
    createRippleEffect(nodeX, nodeY, nodeGroup);

    // Calculate zoom to 7x for very close view
    const targetScale = 7.0;
    const base = zoomPanState.baseViewBox;

    // Calculate new viewBox dimensions
    const newWidth = base.width / targetScale;
    const newHeight = base.height / targetScale;

    // Center the viewBox on the node
    const newX = nodeX - newWidth / 2;
    const newY = nodeY - newHeight / 2;

    // Apply smooth cinematic zoom/pan using GSAP for proper viewBox animation
    gsap.to(svg, {
        duration: 1.2,
        ease: "power2.inOut",
        attr: { viewBox: `${newX} ${newY} ${newWidth} ${newHeight}` },
        onUpdate: function() {
            // Update state during animation
            const currentViewBox = svg.getAttribute('viewBox').split(' ').map(parseFloat);
            zoomPanState.currentX = currentViewBox[0];
            zoomPanState.currentY = currentViewBox[1];
        },
        onComplete: function() {
            // Ensure final state is set
            zoomPanState.currentScale = targetScale;
            zoomPanState.currentX = newX;
            zoomPanState.currentY = newY;
        }
    });

    // Hide decorative images when zooming to a node
    updateImageVisibility(targetScale);

    updateZoomButtons();

    // Close search panel after animation
    setTimeout(() => {
        document.getElementById('search-panel').classList.remove('active');
    }, 1400);

    // Show modal after zoom animation completes
    if (showModal) {
        console.log('showModal is true, will show modal in 1300ms');
        setTimeout(() => {
            console.log('Now showing modal for:', name, generation);
            const parentsStr = nodeGroup.getAttribute('data-person-parents');
            const parents = parentsStr ? JSON.parse(parentsStr) : [];
            showPersonInfo(name, generation, parents);
        }, 1300); // Show modal after smooth cinematic zoom completes
    } else {
        console.log('showModal is false, not showing modal');
    }
}

// Global variable to track active ripple interval
let activeRippleInterval = null;

// Create continuous ripple effect with concentric circles
function createRippleEffect(x, y, highlightedNode) {
    const treeGroup = document.getElementById('tree-group');
    if (!treeGroup) return;

    // Clear any existing ripple interval
    if (activeRippleInterval) {
        clearInterval(activeRippleInterval);
        activeRippleInterval = null;
    }

    // Get the circle element from the highlighted node for bounce effect
    const nodeCircle = highlightedNode ? highlightedNode.querySelector('circle') : null;
    const originalStrokeWidth = nodeCircle ? nodeCircle.getAttribute('stroke-width') || '6' : '6';

    // Function to create bounce effect on the node's outline
    function bounceNodeOutline() {
        if (!nodeCircle) return;

        const bounceStartTime = Date.now();
        const bounceDuration = 300; // 300ms bounce animation

        function animateBounce() {
            const elapsed = Date.now() - bounceStartTime;
            const progress = Math.min(elapsed / bounceDuration, 1);

            if (progress < 0.5) {
                // First half: compress inward (increase stroke width, making it look smaller)
                const compressionProgress = progress * 2; // 0 to 1 in first half
                const strokeWidth = parseFloat(originalStrokeWidth) + (compressionProgress * 8); // Increase by 8
                nodeCircle.setAttribute('stroke-width', strokeWidth);
            } else {
                // Second half: release outward (return to normal)
                const releaseProgress = (progress - 0.5) * 2; // 0 to 1 in second half
                const strokeWidth = parseFloat(originalStrokeWidth) + 8 - (releaseProgress * 8);
                nodeCircle.setAttribute('stroke-width', strokeWidth);
            }

            if (progress < 1) {
                requestAnimationFrame(animateBounce);
            } else {
                // Ensure we're back to the highlighted stroke width
                nodeCircle.setAttribute('stroke-width', originalStrokeWidth);
            }
        }

        requestAnimationFrame(animateBounce);
    }

    // Function to create a wave of 2 concentric ripple circles
    function createRippleWave() {
        // Start bounce effect
        bounceNodeOutline();

        // Launch ripples after a slight delay (when bounce is releasing)
        setTimeout(() => {
            // Create 2 concentric rings slightly staggered
            for (let i = 0; i < 2; i++) {
                setTimeout(() => {
                    const ripple = document.createElementNS(svgNS, 'circle');
                    ripple.setAttribute('cx', x);
                    ripple.setAttribute('cy', y);
                    ripple.setAttribute('r', 40);
                    ripple.setAttribute('fill', 'none');
                    ripple.setAttribute('stroke', '#4ade80');
                    ripple.setAttribute('stroke-width', '4');
                    ripple.setAttribute('opacity', '1');
                    ripple.classList.add('ripple-circle');

                    treeGroup.appendChild(ripple);

                    const startTime = Date.now();

                    function animateRipple() {
                        const elapsed = Date.now() - startTime;
                        const duration = 2000; // 2 seconds

                        const progress = Math.min(elapsed / duration, 1);

                        // Expand radius
                        const radius = 40 + (progress * 200); // 40 to 240
                        ripple.setAttribute('r', radius);

                        // Fade out
                        const opacity = 1 - progress;
                        ripple.setAttribute('opacity', opacity);

                        // Thin out stroke
                        const strokeWidth = 4 - (progress * 3); // 4 to 1
                        ripple.setAttribute('stroke-width', Math.max(strokeWidth, 0.5));

                        if (progress < 1) {
                            requestAnimationFrame(animateRipple);
                        } else {
                            ripple.remove();
                        }
                    }

                    requestAnimationFrame(animateRipple);
                }, i * 333); // Stagger by 333ms to create concentric effect
            }
        }, 150); // Launch ripples at peak compression (halfway through bounce)
    }

    // Create initial wave
    createRippleWave();

    // Set up continuous ripple waves every 2 seconds
    activeRippleInterval = setInterval(createRippleWave, 2000);
}

// Clear search highlights
function clearSearchHighlights() {
    // Clear the ripple interval first to stop new ripples from being created
    if (activeRippleInterval) {
        clearInterval(activeRippleInterval);
        activeRippleInterval = null;
    }

    // Fade out existing ripple circles
    document.querySelectorAll('.ripple-circle').forEach(ripple => {
        const currentOpacity = parseFloat(ripple.getAttribute('opacity') || '1');
        const startTime = Date.now();
        const fadeDuration = 900; // 900ms fade out

        function fadeOutRipple() {
            const elapsed = Date.now() - startTime;
            const progress = Math.min(elapsed / fadeDuration, 1);

            // Fade out from current opacity to 0
            const newOpacity = currentOpacity * (1 - progress);
            ripple.setAttribute('opacity', newOpacity);

            if (progress < 1) {
                requestAnimationFrame(fadeOutRipple);
            } else {
                ripple.remove();
            }
        }

        requestAnimationFrame(fadeOutRipple);
    });

    // Fade out the highlighted node outline
    document.querySelectorAll('.person-node.highlighted').forEach(node => {
        const circle = node.querySelector('circle');
        if (circle) {
            const startTime = Date.now();
            const fadeDuration = 900; // 900ms fade out
            const startStrokeWidth = parseFloat(circle.getAttribute('stroke-width') || '6');
            const startStroke = circle.getAttribute('stroke');
            const startFilter = circle.style.filter || window.getComputedStyle(circle).filter;

            function fadeOutNode() {
                const elapsed = Date.now() - startTime;
                const progress = Math.min(elapsed / fadeDuration, 1);

                // Fade out stroke width back to normal (2)
                const strokeWidth = startStrokeWidth - (progress * (startStrokeWidth - 2));
                circle.setAttribute('stroke-width', strokeWidth);

                // Fade out the drop shadow filter
                const shadowOpacity = 1 - progress;
                circle.style.filter = `drop-shadow(0 0 15px rgba(74, 222, 128, ${shadowOpacity * 0.9}))`;

                if (progress < 1) {
                    requestAnimationFrame(fadeOutNode);
                } else {
                    // Reset to default state
                    circle.setAttribute('stroke-width', '2');
                    circle.style.filter = '';
                    node.classList.remove('highlighted');
                }
            }

            requestAnimationFrame(fadeOutNode);
        } else {
            node.classList.remove('highlighted');
        }
    });
}

