                            // Convert 0.1cm to angular offset: arc_length / radius
                            // 0.1cm = 3.78 pixels (at 37.8 pixels/cm), scaled by 1.07
                            const arcLength = 4.0446;
                            const angularOffset = arcLength / radius;
                            angle += angularOffset; // Add to move anticlockwise (left)
