// Perform the actual lineage highlighting
function performLineageHighlighting(person) {
    clearLineageHighlighting();

    // Build lineage path (from root to current person)
    const lineagePath = [];
    let currentPerson = person;

    // Build path backwards from person to root
    while (currentPerson) {
        lineagePath.unshift({
            name: currentPerson.name,
            generation: currentPerson.generation,
            parents: currentPerson.parents
        });

        // Find parent
        if (currentPerson.parents && currentPerson.parents.length > 0) {
            const parentGen = currentPerson.generation - 1;
            const parent = familyData.find(p =>
                p.generation === parentGen &&
                JSON.stringify(p.parents) === JSON.stringify(currentPerson.parents.slice(0, -1))
            );
            currentPerson = parent;
        } else {
            currentPerson = null;
        }
    }

    console.log('Lineage path:', lineagePath);

    // Highlight all nodes in the lineage path
    const highlightedNodes = [];
    lineagePath.forEach(ancestor => {
        const parentsStr = JSON.stringify(ancestor.parents);
        const nodeGroup = document.querySelector(
            `.person-node[data-person-name="${ancestor.name}"][data-person-generation="${ancestor.generation}"][data-person-parents='${parentsStr}']`
        );

        if (nodeGroup) {
            nodeGroup.classList.add('highlighted');
            highlightedNodes.push(nodeGroup);
        }
    });

    console.log('Highlighted nodes:', highlightedNodes.length);

    // Restart ripple animation on the current person's node
    if (highlightedNodes.length > 0) {
        const currentPersonNode = highlightedNodes[highlightedNodes.length - 1]; // Last node is the current person
        const circle = currentPersonNode.querySelector('circle');
        if (circle) {
            const nodeX = parseFloat(circle.getAttribute('cx'));
            const nodeY = parseFloat(circle.getAttribute('cy'));
            // Restart the ripple effect
            createRippleEffect(nodeX, nodeY, currentPersonNode);
        }
    }

    // Highlight connecting branches between lineage nodes
    const svg = document.getElementById('tree-canvas');
    if (!svg) return;

    const allLines = svg.querySelectorAll('line');

    // For each pair of consecutive nodes in lineage, find and highlight the connecting line
    for (let i = 0; i < highlightedNodes.length - 1; i++) {
        const node1 = highlightedNodes[i];
        const node2 = highlightedNodes[i + 1];

        const circle1 = node1.querySelector('circle');
        const circle2 = node2.querySelector('circle');

        if (circle1 && circle2) {
            const x1 = parseFloat(circle1.getAttribute('cx'));
            const y1 = parseFloat(circle1.getAttribute('cy'));
            const x2 = parseFloat(circle2.getAttribute('cx'));
            const y2 = parseFloat(circle2.getAttribute('cy'));

            console.log(`Looking for branch between ${lineagePath[i].name} (${x1},${y1}) and ${lineagePath[i+1].name} (${x2},${y2})`);

            // Tolerance for checking if line endpoint is near a node
            const tolerance = 60;

            allLines.forEach(line => {
                const lx1 = parseFloat(line.getAttribute('x1'));
                const ly1 = parseFloat(line.getAttribute('y1'));
                const lx2 = parseFloat(line.getAttribute('x2'));
                const ly2 = parseFloat(line.getAttribute('y2'));

                // Check if line endpoint 1 is near node1
                const line1NearNode1 = Math.abs(lx1 - x1) < tolerance && Math.abs(ly1 - y1) < tolerance;
                // Check if line endpoint 1 is near node2
                const line1NearNode2 = Math.abs(lx1 - x2) < tolerance && Math.abs(ly1 - y2) < tolerance;

                // Check if line endpoint 2 is near node1
                const line2NearNode1 = Math.abs(lx2 - x1) < tolerance && Math.abs(ly2 - y1) < tolerance;
                // Check if line endpoint 2 is near node2
                const line2NearNode2 = Math.abs(lx2 - x2) < tolerance && Math.abs(ly2 - y2) < tolerance;

                // STRICT: Line must connect these two specific nodes and ONLY these two nodes
                // One endpoint must be near node1 AND the other endpoint must be near node2
                const connectsTheseNodesOnly = (
                    (line1NearNode1 && line2NearNode2) ||
                    (line1NearNode2 && line2NearNode1)
                );

                if (connectsTheseNodesOnly) {
                    line.classList.add('lineage-highlighted');
                    console.log(`Found direct lineage branch: (${lx1},${ly1}) to (${lx2},${ly2})`);
                }
            });
        }
    }

    // Calculate bounding box of all highlighted nodes
    if (highlightedNodes.length > 0) {
        let minX = Infinity, minY = Infinity, maxX = -Infinity, maxY = -Infinity;

        highlightedNodes.forEach(node => {
            const circle = node.querySelector('circle');
            if (circle) {
                const cx = parseFloat(circle.getAttribute('cx'));
                const cy = parseFloat(circle.getAttribute('cy'));
                const r = parseFloat(circle.getAttribute('r')) || 40;

                minX = Math.min(minX, cx - r);
                minY = Math.min(minY, cy - r);
                maxX = Math.max(maxX, cx + r);
                maxY = Math.max(maxY, cy + r);
            }
        });

        // Add padding around the lineage path
        const padding = 200;
        minX -= padding;
        minY -= padding;
        maxX += padding;
        maxY += padding;

        const width = maxX - minX;
        const height = maxY - minY;

        // Apply smooth cinematic zoom to show the lineage path using GSAP
        gsap.to(svg, {
            duration: 1.2,
            ease: "power2.inOut",
            attr: { viewBox: `${minX} ${minY} ${width} ${height}` },
            onUpdate: function() {
                // Update state during animation
                const currentViewBox = svg.getAttribute('viewBox').split(' ').map(parseFloat);
                if (typeof zoomPanState !== 'undefined') {
                    zoomPanState.currentX = currentViewBox[0];
                    zoomPanState.currentY = currentViewBox[1];
                }
            },
            onComplete: function() {
                // Ensure final state is set
                if (typeof zoomPanState !== 'undefined') {
                    const baseViewBox = zoomPanState.baseViewBox;
                    zoomPanState.currentScale = baseViewBox.width / width;
                    zoomPanState.currentX = minX;
                    zoomPanState.currentY = minY;
                }
            }
        });
    }
}

