svg.style.cursor = 'grab';

// Add event listeners for zoom and pan
// Mouse wheel zoom
svg.addEventListener('wheel', (e) => {
    e.preventDefault();
    const direction = e.deltaY < 0 ? 'in' : 'out';
    handleZoom(direction);
});

// Mouse drag for pan
svg.addEventListener('mousedown', startDrag);
document.addEventListener('mousemove', doDrag);
document.addEventListener('mouseup', endDrag);
svg.addEventListener('mouseleave', endDrag);

// Touch events for mobile
svg.addEventListener('touchstart', handleTouchStart, { passive: false });
svg.addEventListener('touchmove', handleTouchMove, { passive: false });
svg.addEventListener('touchend', handleTouchEnd);
svg.addEventListener('touchcancel', handleTouchEnd);

// Navigate to a lineage member with ripple animation
function navigateToLineageMember(name, generation) {
    console.log('Navigating to lineage member:', name, generation);

    // Close the current modal first
    closePersonInfoModal();

    // Wait for modal to close, then navigate with ripple effect
    setTimeout(() => {
        // Call zoomToNode with showModal=true to get ripple animation and then show modal
        zoomToNode(name, generation, null, true);
    }, 500); // Wait for close animation to complete
}

// Make navigateToLineageMember globally accessible
window.navigateToLineageMember = navigateToLineageMember;

// Keyboard shortcuts
document.addEventListener('keydown', (e) => {
    if (e.target.tagName === 'INPUT' || e.target.tagName === 'TEXTAREA') {
        return; // Don't trigger if typing in input field
    }

    if (e.key === '+' || e.key === '=') {
        e.preventDefault();
        handleZoom('in');
    } else if (e.key === '-') {
        e.preventDefault();
        handleZoom('out');
    } else if (e.key === '0') {
        e.preventDefault();
        handleZoom('reset');
    }
});

// Prevent text selection during drag
svg.addEventListener('selectstart', (e) => {
    if (zoomPanState.isDragging) {
        e.preventDefault();
    }
});

// Debounced resize handler to recalculate tree when viewport dimensions change
let resizeTimeout;
let lastWidth = window.innerWidth;
let lastHeight = window.innerHeight;

window.addEventListener('resize', () => {
    clearTimeout(resizeTimeout);
    resizeTimeout = setTimeout(() => {
        const currentWidth = window.innerWidth;
        const currentHeight = window.innerHeight;

        // Only redraw if dimensions actually changed (not just browser chrome changes)
        if (currentWidth !== lastWidth || currentHeight !== lastHeight) {
            lastWidth = currentWidth;
            lastHeight = currentHeight;

            drawTree();
            initializeZoomPan(); // Recalculate baseViewBox

            // Reset zoom state
            zoomPanState.currentScale = 1.0;
            zoomPanState.currentX = zoomPanState.baseViewBox.x;
            zoomPanState.currentY = zoomPanState.baseViewBox.y;
            updateImageVisibility(1.0);
            updateZoomButtons();
        }
    }, 250); // 250ms debounce to avoid excessive redraws
});

// Add orientation change listener for mobile devices (iPad, tablets, phones)
// Only redraw on orientation changes (portrait <-> landscape) as these are significant layout changes
window.addEventListener('orientationchange', () => {
    // Small delay to ensure viewport dimensions are updated
    setTimeout(() => {
        drawTree();
        initializeZoomPan(); // Recalculate baseViewBox

        // Reset zoom state
        zoomPanState.currentScale = 1.0;
        zoomPanState.currentX = zoomPanState.baseViewBox.x;
        zoomPanState.currentY = zoomPanState.baseViewBox.y;
        updateImageVisibility(1.0);
        updateZoomButtons();
    }, 100);
});

